mapper.select:
SELECT id, infoNum, name, imgUrl, type, ROUND ( 6378.138 * 2 * ASIN( SQRT( POW( SIN( ( #{latitude} * PI() / 180 - latitude * PI() / 180 ) / 2 ), 2 ) + COS(#{latitude} * PI() / 180) * COS(latitude * PI() / 180) * POW( SIN( ( #{longitude} * PI() / 180 - longitude * PI() / 180 ) / 2 ), 2 ) ) ) * 1000 ) AS distance, address, order_count, price, savePrice, marketPrice, latitude, longitude, shoots FROM gas_info where type = #{type}
ORDER BY price-savePrice
ORDER BY distance
LIMIT ${(page-1)*pagesize},${pagesize}

mapper.update:
  		UPDATE gas_info
		SET price = #{price} 
		where 
		id = #{id}
