mapper.select:
SELECT go.`id`, go.`info_id`, go.`phone`, go.`type`, go.`shootNum`, go.`real_price`, go.`save_price`, go.`insertDate`, DATE_FORMAT(go.time_end,'%Y-%c-%d %h:%i:%s') AS time_end, go.`transaction_id`, go.`bank_type`, go.`out_trade_no`, go.`openid`, gi.`name` FROM gas_order go LEFT JOIN gas_info gi ON go.info_id = gi.id where go.phone = #{phone}
and gi.id = #{id}
order by time_end desc LIMIT ${(page-1)*pagesize},${pagesize}

mapper.update:
  		update gas_account
		SET balance = balance + ${jine}
		WHERE
			infoNum = #{infoNum}
